import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [decks, setDecks] = useState<any[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDecks = async () => {
      try {
        const response = await axios.get('http://localhost:3000/decks');
        setDecks(response.data);
      } catch (error) {
        console.error('Erro ao buscar decks:', error);
      }
    };

    fetchDecks();
  }, []);

  const handleCreateDeck = () => {
    navigate('/create-deck');
  };

  return (
    <div className="dashboard-container min-h-screen bg-gray-900 text-white">
      {/* Barra de Navegação */}
      <nav className="bg-black p-4 flex justify-between items-center shadow-md">
        <h1 className="text-3xl font-bold text-orange-500">MagicCards Dashboard</h1>
        <div className="flex space-x-4">
          <button
            onClick={handleCreateDeck}
            className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded-lg transition duration-300"
          >
            Criar Novo Deck
          </button>
        </div>
      </nav>

      {/* Lista de Decks */}
      <div className="p-8">
        <h2 className="text-2xl font-semibold mb-4">Meus Decks</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {decks.length > 0 ? (
            decks.map((deck) => (
              <div
                key={deck.id}
                className="deck-card bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 transform hover:scale-105"
              >
                <img
                  src={deck.commanderImage || '/images/default-commander.jpg'}
                  alt={deck.name}
                  className="h-32 w-full object-cover rounded-md mb-4"
                />
                <h3 className="text-xl font-bold mb-2">{deck.name}</h3>
                <p className="text-gray-400 mb-2">Comandante: {deck.commanderName}</p>
                <p className="text-gray-400">Cartas: {deck.cards.length}</p>
              </div>
            ))
          ) : (
            <p className="text-gray-500">Nenhum deck encontrado. Clique em "Criar Novo Deck" para adicionar um.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
