import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css'; // Importa o arquivo CSS

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Lista de imagens para o background
  const backgroundImages = [
    require('../assets/1314195.jpg'),
    require('../assets/1315735.jpg'),
    require('../assets/1303446.jpg'),
    require('../assets/1314053.jpg'),
    require('../assets/1290838.jpg'),
    require('../assets/1302423.jpg'),
  ];

  // Escolher uma imagem aleatória
  const [backgroundImage, setBackgroundImage] = useState('');
  useEffect(() => {
    const randomImage = backgroundImages[Math.floor(Math.random() * backgroundImages.length)];
    setBackgroundImage(randomImage);
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // Requisição à API para login
      const response = await axios.post('http://localhost:3000/auth/login', { email, password });
      console.log('Login bem-sucedido:', response.data);
      // Armazenar token e redirecionar para o Dashboard
      navigate('/dashboard');
    } catch (error) {
      console.error('Erro no login:', error);
    }
  };

  return (
    <div className="login-container">
      {/* Lado esquerdo: Formulário de Login */}
      <div className="login-form-container">
        <h1 className="login-title">Bem-vindo(a) de volta</h1>
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email" className="login-label">Email</label>
            <input
              id="email"
              type="email"
              placeholder="Digite seu email"
              className="login-input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="login-label">Senha</label>
            <input
              id="password"
              type="password"
              placeholder="Digite sua senha"
              className="login-input"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="login-button">
            Entrar
          </button>
          <p className="login-register">
            Não tem uma conta?{' '}
            <a href="/register" className="login-link">
              Criar Conta
            </a>
          </p>
        </form>
      </div>

      {/* Lado direito: Imagem de fundo e logo */}
      <div className="login-background">
        <img
          src={require('../assets/Magic-The-Gathering-Logo.png')}
          alt="Magic The Gathering Logo"
          className="login-logo"
        />
      </div>
    </div>
  );
};

export default Login;
