import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // Requisição à API para login
      const response = await axios.post('http://localhost:3000/auth/login', { email, password });
      console.log('Login bem-sucedido:', response.data);
      // Armazenar token e redirecionar para o Dashboard
      navigate('/dashboard');
    } catch (error) {
      console.error('Erro no login:', error);
    }
  };

  return (
    <div className="flex h-screen">
      {/* Lado esquerdo: Formulário de Login */}
      <div className="w-1/2 flex flex-col justify-center items-center bg-white p-10 shadow-md">
        <h1 className="text-3xl font-bold mb-6">Bem-vindo(a) de volta</h1>
        <form className="w-full max-w-sm" onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
              Email
            </label>
            <input
              id="email"
              type="email"
              placeholder="Digite seu email"
              className="w-full px-3 py-2 border rounded-lg text-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
              Senha
            </label>
            <input
              id="password"
              type="password"
              placeholder="Digite sua senha"
              className="w-full px-3 py-2 border rounded-lg text-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded transition duration-300"
          >
            Entrar
          </button>
          <p className="mt-4 text-center text-gray-600">
            Não tem uma conta?{' '}
            <a href="/register" className="text-indigo-500 hover:underline">
              Criar Conta
            </a>
          </p>
        </form>
      </div>
      
      {/* Lado direito: Imagens de fundo e logo */}
      <div className="w-1/2 relative flex justify-center items-center overflow-hidden">
        <img
          src={require('../assets/Magic-The-Gathering-Logo.png')}
          alt="Magic The Gathering Logo"
          className="max-w-xs filter drop-shadow-lg opacity-90 z-10"
        />
        <div className="absolute inset-0 grid grid-cols-3 grid-rows-2 gap-4">
          <img src={require('../assets/1314195.jpg')} alt="background-1" className="opacity-20 transform scale-100 hover:scale-105 transition duration-700" />
          <img src={require('../assets/1315735.jpg')} alt="background-2" className="opacity-20 transform scale-100 hover:scale-105 transition duration-700" />
          <img src={require('../assets/1303446.jpg')} alt="background-3" className="opacity-20 transform scale-100 hover:scale-105 transition duration-700" />
          <img src={require('../assets/1314053.jpg')} alt="background-4" className="opacity-20 transform scale-100 hover:scale-105 transition duration-700" />
          <img src={require('../assets/1290838.jpg')} alt="background-5" className="opacity-20 transform scale-100 hover:scale-105 transition duration-700" />
          <img src={require('../assets/1302423.jpg')} alt="background-6" className="opacity-20 transform scale-100 hover:scale-105 transition duration-700" />
        </div>
      </div>
    </div>
  );
};

export default Login;
